package com.keduo.entity;

import java.sql.Date;

public class Bill {
	private int billId;
	private String billtype;		//费用类型
	private double bill;			//费用金额
	private Date billtime;			//费用产生日期
	private int shop;				//商家id
	private String feedbackorder;	//回馈单单号
	public Bill() {
		super();
		// TODO Auto-generated constructor stub
	}
	public Bill(int billId, String billtype, double bill, Date billtime, int shop, String feedbackorder) {
		super();
		this.billId = billId;
		this.billtype = billtype;
		this.bill = bill;
		this.billtime = billtime;
		this.shop = shop;
		this.feedbackorder = feedbackorder;
	}
	public int getBillId() {
		return billId;
	}
	public void setBillId(int billId) {
		this.billId = billId;
	}
	public String getBilltype() {
		return billtype;
	}
	public void setBilltype(String billtype) {
		this.billtype = billtype;
	}
	public double getBill() {
		return bill;
	}
	public void setBill(double bill) {
		this.bill = bill;
	}
	public Date getBilltime() {
		return billtime;
	}
	public void setBilltime(Date billtime) {
		this.billtime = billtime;
	}
	public int getShop() {
		return shop;
	}
	public void setShop(int shop) {
		this.shop = shop;
	}
	public String getFeedbackorder() {
		return feedbackorder;
	}
	public void setFeedbackorder(String feedbackorder) {
		this.feedbackorder = feedbackorder;
	}
	@Override
	public String toString() {
		return "Bill [billId=" + billId + ", billtype=" + billtype + ", bill=" + bill + ", billtime=" + billtime
				+ ", shop=" + shop + ", feedbackorder=" + feedbackorder + "]";
	}
	
}
